class TagGroupManager {
    constructor() {
        this.groups = new Map();
        this.sets = new Map();
        this.currentSet = 'default';
        this.searchQuery = '';
        this.editMode = false;
        this.selectedTags = new Set();

        this.loadData();
        this.initializeUI();
        this.bindEvents();
    }

    loadData() {
        try {
            const data = localStorage.getItem('tag-group-manager-data');
            if (data) {
                const parsed = JSON.parse(data);
                this.groups = new Map(parsed.groups || []);
                this.sets = new Map(parsed.sets || []);
                this.currentSet = parsed.currentSet || 'default';
            }
        } catch (error) {
            console.error('Failed to load Tag Group Manager data:', error);
        }
    }

    saveData() {
        try {
            const data = {
                groups: Array.from(this.groups.entries()),
                sets: Array.from(this.sets.entries()),
                currentSet: this.currentSet
            };
            localStorage.setItem('tag-group-manager-data', JSON.stringify(data));
        } catch (error) {
            console.error('Failed to save Tag Group Manager data:', error);
        }
    }

    initializeUI() {
        this.createSidebar();
        this.render();
    }

    createSidebar() {
        const leaf = this.getWorkspaceLeaf();
        if (!leaf) {
            console.error('Could not find workspace leaf for Tag Group Manager');
            return;
        }

        const container = leaf.containerEl;
        container.empty();

        const rootDiv = container.createDiv('tag-group-manager-container');
        this.container = rootDiv;

        // æ ‡é¢˜
        const title = rootDiv.createDiv('tag-group-manager-title');
        title.textContent = 'ğŸ·ï¸ æ ‡ç­¾ç»„ç®¡ç†';

        // é›†é€‰æ‹©å™¨
        this.setSelector = rootDiv.createDiv('tag-group-set-selector');

        // æœç´¢æ¡†
        this.searchContainer = rootDiv.createDiv('tag-search-container');
        this.searchInput = this.searchContainer.createEl('input', {
            type: 'text',
            placeholder: 'æœç´¢æ ‡ç­¾...',
            cls: 'tag-search-input'
        });

        // æ ‡ç­¾ç»„å®¹å™¨
        this.groupsContainer = rootDiv.createDiv();

        // æ“ä½œæŒ‰é’®
        this.actionsContainer = rootDiv.createDiv('tag-manager-actions');
        this.createActionButtons();

        // æ·»åŠ ç»„æŒ‰é’®
        this.addGroupButton = rootDiv.createEl('button', {
            text: '+ æ·»åŠ æ ‡ç­¾ç»„',
            cls: 'add-group-button'
        });
    }

    createActionButtons() {
        // åˆ·æ–°æŒ‰é’®
        const refreshButton = this.actionsContainer.createEl('button', {
            text: 'ğŸ”„ åˆ·æ–°',
            cls: 'action-button'
        });
        refreshButton.onclick = () => this.render();

        // ç¼–è¾‘æ¨¡å¼æŒ‰é’®
        const editButton = this.actionsContainer.createEl('button', {
            text: 'âœï¸ ç¼–è¾‘',
            cls: 'action-button primary'
        });
        editButton.onclick = () => this.toggleEditMode();

        // æ‰¹é‡æ“ä½œæŒ‰é’®
        const bulkButton = this.actionsContainer.createEl('button', {
            text: 'ğŸ“¦ æ‰¹é‡æ“ä½œ',
            cls: 'action-button'
        });
        bulkButton.onclick = () => this.toggleBulkMode();

        // å¯¼å…¥å¯¼å‡ºæŒ‰é’®
        const exportButton = this.actionsContainer.createEl('button', {
            text: 'ğŸ“¤ å¯¼å‡º',
            cls: 'action-button'
        });
        exportButton.onclick = () => this.exportData();
    }

    getWorkspaceLeaf() {
        // å°è¯•è·å–ä¾§è¾¹æ å¶å­èŠ‚ç‚¹
        const workspace = app.workspace;
        const leaf = workspace.getLeavesOfType('tag-group-manager');
        if (leaf.length > 0) {
            return leaf[0];
        }

        // å¦‚æœæ²¡æœ‰ï¼Œåˆ›å»ºä¸€ä¸ªæ–°çš„
        try {
            return workspace.getLeaf(true);
        } catch (error) {
            console.error('Failed to create leaf for Tag Group Manager:', error);
            return null;
        }
    }

    render() {
        this.renderSetSelector();
        this.renderGroups();
    }

    renderSetSelector() {
        this.setSelector.empty();

        // é»˜è®¤é›†åˆ
        const defaultSetButton = this.setSelector.createEl('button', {
            text: 'ğŸ“‹ å…¨éƒ¨æ ‡ç­¾',
            cls: 'set-button'
        });
        if (this.currentSet === 'default') {
            defaultSetButton.classList.add('active');
        }
        defaultSetButton.onclick = () => this.switchSet('default');

        // è‡ªå®šä¹‰é›†åˆ
        this.sets.forEach((set, setName) => {
            const setButton = this.setSelector.createEl('button', {
                text: `${set.icon || 'ğŸ“'} ${setName}`,
                cls: 'set-button'
            });
            if (this.currentSet === setName) {
                setButton.classList.add('active');
            }
            setButton.onclick = () => this.switchSet(setName);
        });
    }

    renderGroups() {
        this.groupsContainer.empty();

        // è¿‡æ»¤æ ‡ç­¾ç»„
        let filteredGroups = Array.from(this.groups.values());

        if (this.currentSet !== 'default') {
            const currentSet = this.sets.get(this.currentSet);
            if (currentSet) {
                filteredGroups = filteredGroups.filter(group =>
                    currentSet.groups.includes(group.id)
                );
            }
        }

        // æœç´¢è¿‡æ»¤
        if (this.searchQuery) {
            filteredGroups = filteredGroups.filter(group =>
                group.name.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
                group.tags.some(tag => tag.toLowerCase().includes(this.searchQuery.toLowerCase()))
            );
        }

        // æ·»åŠ ç»„æŒ‰é’®
        this.addGroupButton.onclick = () => this.addGroup();

        // æ¸²æŸ“æ¯ä¸ªç»„
        filteredGroups.forEach(group => {
            this.renderGroup(group);
        });

        // æ²¡æœ‰ç»„æ—¶çš„æç¤º
        if (filteredGroups.length === 0) {
            const emptyMessage = this.groupsContainer.createDiv();
            emptyMessage.textContent = 'æš‚æ— æ ‡ç­¾ç»„ï¼Œç‚¹å‡»ä¸Šæ–¹æŒ‰é’®åˆ›å»º';
            emptyMessage.style.color = 'var(--text-muted)';
            emptyMessage.style.textAlign = 'center';
            emptyMessage.style.padding = '2rem';
        }
    }

    renderGroup(group) {
        const groupDiv = this.groupsContainer.createDiv('tag-group');

        // ç»„å¤´éƒ¨
        const headerDiv = groupDiv.createDiv('tag-group-header');

        const nameDiv = headerDiv.createDiv('tag-group-name');
        nameDiv.textContent = group.name;

        const countDiv = headerDiv.createDiv('tag-group-count');
        countDiv.textContent = `${group.tags.length}`;

        // æ ‡ç­¾åˆ—è¡¨
        const tagsDiv = groupDiv.createDiv('tag-list');

        group.tags.forEach(tag => {
            const tagDiv = tagsDiv.createDiv('tag-item');
            tagDiv.textContent = tag;

            if (this.editMode) {
                tagDiv.style.cursor = 'move';
                tagDiv.draggable = true;
                tagDiv.addEventListener('dragstart', (e) => this.handleDragStart(e, tag));
                tagDiv.addEventListener('dragover', (e) => this.handleDragOver(e));
                tagDiv.addEventListener('drop', (e) => this.handleDrop(e, tag));
                tagDiv.addEventListener('dragend', (e) => this.handleDragEnd(e));

                // åŒå‡»ç¼–è¾‘
                tagDiv.addEventListener('dblclick', () => this.editTag(tag, tagDiv));
            }

            // ç‚¹å‡»é€‰æ‹©
            tagDiv.addEventListener('click', () => {
                if (this.editMode) {
                    this.toggleTagSelection(tag, tagDiv);
                } else {
                    this.insertTagToCurrentNote(tag);
                }
            });
        });
    }

    addGroup() {
        const groupName = prompt('è¯·è¾“å…¥æ ‡ç­¾ç»„åç§°:');
        if (!groupName) return;

        const groupId = groupName.toLowerCase().replace(/\s+/g, '-');

        if (this.groups.has(groupId)) {
            alert('è¯¥æ ‡ç­¾ç»„å·²å­˜åœ¨');
            return;
        }

        const newGroup = {
            id: groupId,
            name: groupName,
            tags: [],
            icon: 'ğŸ“‹',
            color: 'var(--text-normal)',
            description: ''
        };

        this.groups.set(groupId, newGroup);
        this.saveData();
        this.render();
    }

    removeGroup(groupId) {
        if (confirm('ç¡®å®šè¦åˆ é™¤è¿™ä¸ªæ ‡ç­¾ç»„å—ï¼Ÿ')) {
            this.groups.delete(groupId);
            this.saveData();
            this.render();
        }
    }

    switchSet(setName) {
        this.currentSet = setName;
        this.saveData();
        this.render();
    }

    toggleEditMode() {
        this.editMode = !this.editMode;
        this.selectedTags.clear();

        const editButton = this.actionsContainer.querySelector('button:nth-child(2)');
        if (this.editMode) {
            editButton.textContent = 'ğŸ’¾ ä¿å­˜';
            editButton.classList.add('primary');
            this.container.classList.add('edit-mode');
        } else {
            editButton.textContent = 'âœï¸ ç¼–è¾‘';
            this.container.classList.remove('edit-mode');
        }

        this.render();
    }

    toggleBulkMode() {
        alert('æ‰¹é‡åŠŸèƒ½æ­£åœ¨å¼€å‘ä¸­...');
    }

    insertTagToCurrentNote(tag) {
        const editor = app.workspace.activeEditor?.editor;
        if (!editor) {
            alert('æ²¡æœ‰æ´»åŠ¨çš„ç¼–è¾‘å™¨');
            return;
        }

        const cursor = editor.getCursor();
        editor.replaceRange(`#${tag} `, cursor);

        // ç§»åŠ¨å…‰æ ‡åˆ°æ–°æ’å…¥çš„æ ‡ç­¾å
        editor.setCursor({
            line: cursor.line,
            ch: cursor.ch + tag.length + 2 // #tag + ç©ºæ ¼
        });
    }

    toggleTagSelection(tag, element) {
        if (this.selectedTags.has(tag)) {
            this.selectedTags.delete(tag);
            element.classList.remove('selected');
        } else {
            this.selectedTags.add(tag);
            element.classList.add('selected');
        }
    }

    editTag(tag, element) {
        const newTag = prompt('ç¼–è¾‘æ ‡ç­¾:', tag);
        if (!newTag || newTag === tag) return;

        // æ›´æ–°æ‰€æœ‰ç»„ä¸­çš„æ ‡ç­¾
        this.groups.forEach(group => {
            const index = group.tags.indexOf(tag);
            if (index !== -1) {
                group.tags[index] = newTag;
            }
        });

        this.saveData();
        this.render();
    }

    handleDragStart(e, tag) {
        e.dataTransfer.setData('text/plain', tag);
        e.target.classList.add('dragging');
    }

    handleDragOver(e) {
        e.preventDefault();
        e.currentTarget.classList.add('drag-over');
    }

    handleDrop(e, targetTag) {
        e.preventDefault();
        const draggedTag = e.dataTransfer.getData('text/plain');

        // è¿™é‡Œå¯ä»¥å®ç°æ ‡ç­¾çš„é‡æ–°æ’åº
        console.log(`æ‹–æ‹½ ${draggedTag} åˆ° ${targetTag}`);

        e.currentTarget.classList.remove('drag-over');
    }

    handleDragEnd(e) {
        e.target.classList.remove('dragging');
        document.querySelectorAll('.drag-over').forEach(el => {
            el.classList.remove('drag-over');
        });
    }

    exportData() {
        const data = {
            groups: Array.from(this.groups.entries()),
            sets: Array.from(this.sets.entries()),
            currentSet: this.currentSet,
            exportDate: new Date().toISOString()
        };

        const blob = new Blob([JSON.stringify(data, null, 2)], {
            type: 'application/json'
        });

        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `tag-groups-${new Date().toISOString().split('T')[0]}.json`;
        a.click();

        URL.revokeObjectURL(url);
    }

    bindEvents() {
        // æœç´¢åŠŸèƒ½
        this.searchInput.addEventListener('input', (e) => {
            this.searchQuery = e.target.value;
            this.render();
        });

        // å¿«æ·é”®
        document.addEventListener('keydown', (e) => {
            if (e.ctrlKey || e.metaKey) {
                switch (e.key) {
                    case 'k':
                        e.preventDefault();
                        this.searchInput.focus();
                        break;
                    case 'n':
                        e.preventDefault();
                        this.addGroup();
                        break;
                }
            }
        });
    }
}

// å½“æ’ä»¶åŠ è½½æ—¶å¯åŠ¨
if (typeof app !== 'undefined') {
    app.workspace.onLayoutReady(() => {
        new TagGroupManager();
    });
} else {
    console.error('Obsidian API not available');
}

